import React from 'react';
import './Header.css'; // CSS 파일 import

function Header(props) {
  const scrollToSection = (id, path) => {
    const element = document.getElementById(id);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
      window.history.pushState(null, '', path); // URL 변경
    }
  };

  return (
    <header className="header">
      <span onClick={() => scrollToSection('Home', '/portfolio')} className="header-item">
        Home
      </span>

      <span onClick={() => scrollToSection('About Me', '/portfolio/product')} className="header-item">
        About Me
      </span>
    </header>
  );
}

export default Header;
